pageData.query:
    query portfolio($relativePath: String!) {
  portfolio(relativePath: $relativePath) {
    ... on Document {
      _sys {
        filename
        basename
        breadcrumbs
        path
        relativePath
        extension
      }
      id
    }
    ...PortfolioParts
  }
}

    fragment PortfolioParts on Portfolio {
  __typename
  title
  global {
    __typename
    ... on PortfolioGlobalMetadata {
      title
      description
      keywords
      image
      twitterHandle
      theme {
        __typename
        mask
        tile
        color
      }
    }
    ... on PortfolioGlobalLayout {
      headerType
      footerType
    }
    ... on PortfolioGlobalNavigation {
      navigation {
        ... on Navigation {
          __typename
          title
          headerMarquee {
            __typename
            marqueeText {
              __typename
              text
            }
            link
          }
          mainNavigation {
            __typename
            navLink {
              __typename
              text
              url
              media {
                __typename
                caption
                source
              }
            }
          }
          subscribe {
            __typename
            title
            description
            linkText
          }
          externalLinksLeft {
            __typename
            link {
              __typename
              text
              url
            }
          }
          externalLinksRight {
            __typename
            link {
              __typename
              text
              url
            }
          }
          footerInformation {
            __typename
            street
            city
            street_2
            city_2
            email {
              __typename
              text
              url
            }
            partnerLogin {
              __typename
              text
              url
            }
          }
        }
        ... on Document {
          _sys {
            filename
            basename
            breadcrumbs
            path
            relativePath
            extension
          }
          id
        }
      }
    }
  }
  sections {
    __typename
    ... on PortfolioSectionsHero {
      title
      subtitle
      noResultText
    }
  }
}
