pageData.query:
    query portfolioExtendTeamAndFocuses($relativePath: String!, $filterTags: [String]!) {
  portfolioSlug(relativePath: $relativePath) {
    ...PortfolioSlugParts
  }
  focusSlugConnection(
    filter: {sections: {focusDetail: {portfolios: {portfolio: {portfolioSlug: {title: {in: $filterTags}}}}}}}
  ) {
    edges {
      node {
        href: title
        sections {
          ... on FocusSlugSectionsFocusDetail {
            images {
              media {
                caption
                source
              }
            }
          }
        }
      }
    }
  }
  teamSlugConnection(
    filter: {sections: {teamMember: {portfolios: {portfolio: {portfolioSlug: {title: {in: $filterTags}}}}}}}
  ) {
    edges {
      node {
        href: title
        sections {
          __typename
          ... on TeamSlugSectionsTeamMember {
            name
            title
            portfolios {
              __typename
              portfolio {
                ... on PortfolioSlug {
                  title
                }
              }
            }
            indexImage {
              caption
              source
            }
          }
        }
      }
    }
  }
}

    fragment PortfolioSlugParts on PortfolioSlug {
  __typename
  title
  global {
    __typename
    ... on PortfolioSlugGlobalMetadata {
      title
      description
      keywords
      image
      twitterHandle
      theme {
        __typename
        mask
        tile
        color
      }
    }
    ... on PortfolioSlugGlobalLayout {
      headerType
      footerType
    }
    ... on PortfolioSlugGlobalNavigation {
      navigation {
        ... on Navigation {
          __typename
          title
          headerMarquee {
            __typename
            marqueeText {
              __typename
              text
            }
            link
          }
          mainNavigation {
            __typename
            navLink {
              __typename
              text
              url
              media {
                __typename
                caption
                source
              }
            }
          }
          subscribe {
            __typename
            title
            description
            linkText
          }
          externalLinksLeft {
            __typename
            link {
              __typename
              text
              url
            }
          }
          externalLinksRight {
            __typename
            link {
              __typename
              text
              url
            }
          }
          footerInformation {
            __typename
            street
            city
            street_2
            city_2
            email {
              __typename
              text
              url
            }
            partnerLogin {
              __typename
              text
              url
            }
          }
        }
        ... on Document {
          _sys {
            filename
            basename
            breadcrumbs
            path
            relativePath
            extension
          }
          id
        }
      }
    }
  }
  sections {
    __typename
    ... on PortfolioSlugSectionsCompany {
      companyName
      link
      description
      image {
        __typename
        caption
        source
        mobileCaption
        mobileSource
      }
      details {
        __typename
        headquarters
        founded
        rreInvested
        rreParticipation
        status
        exitOnly
      }
    }
  }
}
