rrepov.query:
    query rrepov($relativePath: String!) {
  rrepov(relativePath: $relativePath) {
    ... on Document {
      _sys {
        filename
        basename
        breadcrumbs
        path
        relativePath
        extension
      }
      id
    }
    ...RrepovParts
  }
}

    fragment RrepovParts on Rrepov {
  __typename
  title
  global {
    __typename
    ... on RrepovGlobalMetadata {
      title
      description
      keywords
      image
      twitterHandle
      theme {
        __typename
        mask
        tile
        color
      }
    }
    ... on RrepovGlobalLayout {
      headerType
      footerType
    }
    ... on RrepovGlobalNavigation {
      navigation {
        ... on Navigation {
          __typename
          title
          headerMarquee {
            __typename
            marqueeText {
              __typename
              text
            }
            link
          }
          mainNavigation {
            __typename
            navLink {
              __typename
              text
              url
              media {
                __typename
                caption
                source
              }
            }
          }
          subscribe {
            __typename
            title
            description
            linkText
          }
          externalLinksLeft {
            __typename
            link {
              __typename
              text
              url
            }
          }
          externalLinksRight {
            __typename
            link {
              __typename
              text
              url
            }
          }
          footerInformation {
            __typename
            street
            city
            street_2
            city_2
            email {
              __typename
              text
              url
            }
            partnerLogin {
              __typename
              text
              url
            }
          }
        }
        ... on Document {
          _sys {
            filename
            basename
            breadcrumbs
            path
            relativePath
            extension
          }
          id
        }
      }
    }
  }
  sections {
    __typename
    ... on RrepovSectionsHero {
      title
      eyebrow
      description
      podcastLinks {
        __typename
        link {
          __typename
          text
          url
        }
      }
      image {
        __typename
        caption
        source
        mobileCaption
        mobileSource
      }
    }
  }
}
